import {TemplateArgument} from "./TemplateArgument.js"
import {JsTemplateArgument} from "./JsTemplateArgument.js"
import {Register} from "../../genes/Register.js"

const $global = Register.$global

export const LiteralTemplateArgument = Register.global("$hxClasses")["mcl.args.LiteralTemplateArgument"] = 
class LiteralTemplateArgument extends Register.inherits(() => TemplateArgument, true) {
	new(pos, value) {
		this.value = value;
		super.new(null, pos);
	}
	parseValue(value, pos, ctx) {
		if (value == this.value || value.startsWith(this.value + " ")) {
			return {"success": true, "value": value, "raw": this.value};
		};
		return {"success": false};
	}
	static register() {
		TemplateArgument.register("literal", JsTemplateArgument);
	}
	static get __name__() {
		return "mcl.args.LiteralTemplateArgument"
	}
	static get __super__() {
		return TemplateArgument
	}
	get __class__() {
		return LiteralTemplateArgument
	}
}

