import {McbError} from "./McbError.js"
import {Register} from "../../genes/Register.js"

const $global = Register.$global

export const CompilerError = Register.global("$hxClasses")["mcl.error.CompilerError"] = 
class CompilerError extends Register.inherits(McbError) {
	new(message, internal, mcbstack) {
		super.new("" + ((internal) ? "Internal " : "") + "Compiler Error:\n\t" + message, mcbstack);
		this.internal = internal;
	}
	static get __name__() {
		return "mcl.error.CompilerError"
	}
	static get __super__() {
		return McbError
	}
	get __class__() {
		return CompilerError
	}
}

