import {Exception} from "../../haxe/Exception.js"
import {Register} from "../../genes/Register.js"

const $global = Register.$global

export const McbError = Register.global("$hxClasses")["mcl.error.McbError"] = 
class McbError extends Register.inherits(Exception) {
	new(msg, stack) {
		super.new(msg);
		this.mcbstack = stack;
	}
	__init__() {
	}
	static isMclError(e) {
		return ((e) instanceof McbError);
	}
	static get __name__() {
		return "mcl.error.McbError"
	}
	static get __super__() {
		return Exception
	}
	get __class__() {
		return McbError
	}
}

