import {McbError} from "./McbError.js"
import {Register} from "../../genes/Register.js"

const $global = Register.$global

export const ParserError = Register.global("$hxClasses")["mcl.error.ParserError"] = 
class ParserError extends Register.inherits(McbError) {
	new(message) {
		super.new("Parser Error:\n\t" + message, []);
	}
	static get __name__() {
		return "mcl.error.ParserError"
	}
	static get __super__() {
		return McbError
	}
	get __class__() {
		return ParserError
	}
}

